MATCH (n:Country {name: "Finland"})
RETURN n.id, n.name, n.url
ORDER BY n.name;

MATCH (p:Person {name: "George IV of the United Kingdom"})<-[:WROTE]-(c:Comment)<-[:USED]-(b:Browser)
RETURN DISTINCT p.id AS personId, p.name AS personName, b.id AS browserId, b.name AS browserName
ORDER BY b.name;

MATCH (n:Person {name: "Juan_Perón"})-[:ASSOCIATED_WITH]->(c:Category)
RETURN c.name AS category_name, c.id AS category_id, c.url AS category_url
ORDER BY c.name;

MATCH (t:TennisPlayer {name: 'Gastón_Gaudio'})<-[:BELONGS_TO]-(c:Category)
RETURN c.id AS category_id, c.name AS category_name, c.url AS category_url, t.name AS label_name, t.id AS label_id
ORDER BY c.name DESC;

MATCH (p:PrimeMinister)-[:IS_A]->(c:Politician)
RETURN DISTINCT c.name
LIMIT 3;
